// Main.js

import React, { useState } from "react";
import MusicList from "./MusicList";
import Playlist from "./Playlist";

function Main({
  musics,
  playlist,
  addToPlaylist,
  filter,
  setFilter,
  sortBy,
  setSortBy,
  query,
  setQuery,
  onRemove,
}) {
  return (
    <div className="grid grid-cols-5 bg-slate-700 min-h-full">
      <div className="col-span-1">
        <Playlist playlist={playlist} onRemove={onRemove} />
      </div>

      <div className="col-span-4">
        <MusicList
          musics={musics}
          addToPlaylist={addToPlaylist}
          filter={filter}
          setFilter={setFilter}
          sortBy={sortBy}
          setSortBy={setSortBy}
          query={query}
          setQuery={setQuery}
        />
      </div>
    </div>
  );
}

export default Main;
