import MusicItem from "./MusicItem";
import Search from "./Search";
import NumberResult from "./NumberResult";

function MusicList({
  musics,
  addToPlaylist,
  filter,
  setFilter,
  sortBy,
  setSortBy,
  query,
  setQuery,
}) {
  return (
    <div className=" text-white py-7 pl-5">
      <h2 className="text-3xl font-bold pb-4">Music List</h2>

      <Search query={query} setQuery={setQuery} />

      <div>
        <label>
          Filter by Genre:
          <select
            className="bg-slate-600 text-white mx-2"
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
          >
            <option value="all">All</option>
            <option value="pop">Pop</option>
            <option value="rock">Rock</option>
            <option value="jazz">Jazz</option>
          </select>
        </label>
        <label>
          Sort by:
          <select
            className="bg-slate-600 text-white mx-2"
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
          >
            <option value="title">Title</option>
            <option value="artist">Artist</option>
            <option value="genre">Genre</option>
          </select>
        </label>
      </div>
      <NumberResult musics={musics} />

      <ul className="mt-4 grid lg:grid-cols-6 sm:grid-cols-1 gap-4">
        {musics.map((music) => (
          <MusicItem
            key={music.id}
            music={music}
            addToPlaylist={addToPlaylist}
          />
        ))}
      </ul>
    </div>
  );
}

export default MusicList;
