function MusicItem({ music, addToPlaylist }) {
  const handleClick = () => {
    addToPlaylist(music);
  };

  return (
    <div>
      <li key={music.id}>
        {music.title} by {music.artist} ({music.genre}) {"     "}
        <button className="custom-button" onClick={handleClick}>
          <span className="heart-icon">♥️</span>Add to Playlist
        </button>
      </li>
    </div>
  );
}

export default MusicItem;
